import React, { Component } from 'react'
import BingoItem from './BingoItem'
import { FormRow } from '@material-ui/';

export default class BingoRow extends Component {
    constructor(props) {
        super(props)
        this.state = {
            rowItems:this.props.row
        }
    }
    render() {
        return (
            <FormRow>
            <div className="bingo-row">
                <div>{this.state.rowItems.map(i => 
                <BingoItem alignItems="center" alignContent="center" item={i}/>)}</div>
            </div>
            </FormRow>
        )
    }
}
