import React, { Component } from 'react'
import Btn from './Btn'
import '../App.css'

export default class Board extends Component {
    constructor(props) {
        super(props)
        this.state = {
            dimension: this.props.dimension
        }
    }
    render() {
        const {dimension} = this.state
        console.log("BOARD", this.state.slots)
        const dog_cards = this.props.slots.map((slot, i) => {
            <Btn active={ ((this.props.hits || []).includes(slot)) } 
            lined={ ((this.props.hit_lines || []).includes(i)) } 
            slot={slot} position={i} handle-={this.props.handle}/>
        })
        console.log("DOG CARDS", dog_cards)

        return (
            <div style={{
                display: 'block',
                width: 50 * dimension,
                height: 50 * dimension
            }} className="group">
                {dog_cards}
            </div>
        )
    }
}
