import React, { Component } from 'react'
import BingoItem from './BingoItem'
import { Grid } from '@material-ui/core';

import '../App.css'

export default class Card extends Component {
    constructor(props) {
        super(props)
        this.state = {
            rows: []
        }
    }

    addRow = () => {
      const { slots, dimension } = this.props
      console.log(this.props.dimension)
      for (let i = 0; i < dimension; i++) {
        console.log("START SLICE", dimension * (i), "END ", dimension * i + (dimension - 1) )
        this.state.rows.push(
          slots.slice(dimension * i, dimension * i + (dimension - 1) ) 
        )
      }
    }



    render() {
        const { dimension } = this.state
        const card_rows = this.addRow()

        return (
            <Grid
                container
                direction="row"
                justifyContent="center"
                alignItems="center"
                className="group"
            >
                {card_rows}
            </Grid>
        )
    }
}
