import React from 'react'
import { Grid } from '@material-ui/core';
export default class BingoItem extends React.Component{
    constructor(props){
      super(props);
      this.state = {
        slot: this.props.item
      }
    }

    componentDidMount() {
      const slots = document.querySelectorAll('span.text-content')
      for (let i = 0; i < slots; i++) {
        slots[i].innerHTML = this.props.slots[i]
      }
      return slots ;
    }


    render(){
      console.log("SLOTS", this.props.slots)
