import React, { Component } from 'react'
import Btn from './Btn'
import { Grid } from '@material-ui/core';

import '../App.css'

export default class Card extends Component {
    constructor(props) {
        super(props)
        this.state = {
            dimension: this.props.dimension
        }
    }
    render() {
        const {dimension} = this.state
        const card_tiles = 
        this.props.slots.map((slot, i) => 
            <Btn active={((this.props.hits || []).includes(slot))} 
            lined={((this.props.hit_lines || []).includes(i))} 
            idx={i} element={slot} handle={this.props.handle}/>
        )

        return (
            <Grid
                container
                direction="row"
                justifyContent="center"
                alignItems="center"
                className="group"
                xs={2.5}
            >
                {card_tiles}
            </Grid>
        )
    }
}
